example.programlisting:
add_generic()
{
  num_a=$1
  num_b=$2

  expr $1 + $2
}

add_bash()
{
  num_a=$1
  num_b=$2

  echo $(($1 + $2))
}

#! /bin/sh
testAdding() { result=`add_generic 1 2` assertEquals \ "the result of '${result}' was wrong" \ "${result}" 3
# disable non-generic tests [ -z "${BASH_VERSION:-}" ] && startSkipping
result=`add_bash 1 2` assertEquals \ "the result of '${result}' was wrong" \ "${result}" 3 }
